package com.example.yumyum.ui.secondactivity.mealsviewscreen

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.LinearLayoutManager
import com.example.yumyum.R
import com.example.yumyum.data.model.Meal
import com.example.yumyum.databinding.FragmentMealsViewBinding
import com.example.yumyum.ui.secondactivity.homescreen.Cat


class MealsViewFragment : Fragment() {

    lateinit var binding:FragmentMealsViewBinding

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = FragmentMealsViewBinding.inflate(layoutInflater,container,false)
        val adapter = MealAdapter()
        val categories = listOf(
            Meal("0","fried chicken","https://www.tasteofhome.com/wp-content/uploads/2018/01/Crispy-Fried-Chicken_EXPS_TOHJJ22_6445_DR_02_03_11b.jpg"),
            Meal("1","pizza","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTIibPbOeDQQscm9g-fDNdCvROokQJukg8nYQ&s"),
            Meal("2","frence fries","https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQIIdfhNHFpts2r5tUFzCJIHyLC7XRIF5dV8g&s"),
            Meal("3","smoked salmon","data:image/jpeg;base64,")
        )
        adapter.setList(categories)
        binding.mealsRecycler.adapter = adapter
        binding.mealsRecycler.layoutManager = LinearLayoutManager(requireContext())

        return binding.root
    }

}